    private void botaoAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botaoAddActionPerformed
           if(inputDia.getText().equals("")||inputMes.getText().equals("")||inputAno.getText().equals("")){
               JOptionPane.showMessageDialog(null, "Preencha os campos vazios.");
           }
           else{

            Dat3 d1 = new Dat3(Integer.parseInt(inputDia.getText()),Integer.parseInt(inputMes.getText()),Integer.parseInt(inputAno.getText()));
        
            JOptionPane.showMessageDialog(null, "Data atual: " + d1.displayDate()); 

           }
    }//GEN-LAST:event_botaoAddActionPerformed

